sys_dictionary.calculation:
(function calculatedFieldValue(current) {


    var deleteItemQue = new GlideRecord('x_625304_online_0_customer_items');
    deleteItemQue.addQuery('sys_id', 'IN', current.ordered_items);
    deleteItemQue.query();
    while (deleteItemQue.next()) {
        deleteItemQue.deleteRecord();
    }


    var ordersItems = '';

    var que = new GlideRecord('x_625304_online_0_services');
    que.addQuery('sys_id', 'IN', current.services);
    que.query();

    while (que.next()) {
        var itemQue = new GlideRecord('x_625304_online_0_customer_items');
        itemQue.addQuery('vehicle_service', current.sys_id);
        itemQue.addQuery('citem', que.sys_id);
        itemQue.query();
        if (itemQue.next()) {
            itemQue.customer = current.customer;
            itemQue.vehicle_service = current.sys_id;
            itemQue.citem = que.sys_id;
            itemQue.price = parseFloat(que.cost);
            itemQue.name = que.service_name;
            var sysId = itemQue.sys_id;
            itemQue.update();
            if (ordersItems.length === 0) {
                ordersItems = sysId;
            } else {
                ordersItems = ordersItems + ',' + sysId;
            }
        } else {
            var gr = new GlideRecord('x_625304_online_0_customer_items');
            gr.initialize();
            gr.customer = current.customer;
            gr.vehicle_service = current.sys_id;
            gr.citem = que.sys_id;
            gr.price = parseFloat(que.cost);
            gr.name = que.service_name;
            var sysIdd = gr.insert();
            if (ordersItems.length === 0) {
                ordersItems = sysIdd;
            } else {
                ordersItems = ordersItems + ',' + sysIdd;
            }
        }

    }


    return ordersItems; // return the calculated value

})(current);
