sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
   console.log('Azlaan Khan Test');
   if(api.context.props.type==1){
    api.setState('hideMyRequests', false);
   }
   else if(api.context.props.type==2){
    api.setState('hideNewRequests', false);
   }
   else if(api.context.props.type==3){
    api.setState('hideCompletedRequests', false); 
   }

}
