sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {


    var previousState = api.state.selected_services;
    previousState.push(event.payload.sys_id);
    api.setState("selected_services", previousState);

var currentdate = new Date(); 
var datetime = "Last Clicked: " + currentdate.getDate() + "/"
                + (currentdate.getMonth()+1)  + "/" 
                + currentdate.getFullYear() + " @ "  
                + currentdate.getHours() + ":"  
                + currentdate.getMinutes() + ":" 
                + currentdate.getSeconds();

    console.log(previousState);
    //console.log(JSON.stringify(api));
    api.setState('buttonText',datetime);

}
