sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
var assignedMechanic = api.data.service_request.nowRecordFormBlob.gForm.getValue('assigned_mechanic');
console.log('Hello .. '+assignedMechanic);
if(assignedMechanic){
    try{
        api.data.service_request.nowRecordFormBlob.gForm.save();
        helpers.navigate.to('order_success',null,null,true);
    }catch(e){
        api.data.service_request.nowRecordFormBlob.gForm.showErrorBox('assigned_mechanic','Record Not Saved');
    }

}else{
    api.data.service_request.nowRecordFormBlob.gForm.showErrorBox('assigned_mechanic','Please select Mechanic to assign');
}
}
