sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {

    var tableName = 'x_625304_online_0_vehicle_service';
    var recordSysId = api.context.props.serviceId;
    //to set the category to hardware and short description to Hardware
    var encQueryFieldUpdate = 'payment_status=done';

    //update_incident is the name of the Update data resource you're executing
    api.data.update_incident.execute({
        "table": tableName,
        "recordId": recordSysId,
        "templateFields": encQueryFieldUpdate,
        "useSetDisplayValue": false
    });
}
